/* Dtd_PreParse_lex_destroy is for both reentrant and non-reentrant scanners. */
int Dtd_PreParse_lex_destroy  (void)
{
    
    /* Pop the buffer stack, destroying each element. */
	while(YY_CURRENT_BUFFER){
		Dtd_PreParse__delete_buffer(YY_CURRENT_BUFFER  );
		YY_CURRENT_BUFFER_LVALUE = NULL;
		Dtd_PreParse_pop_buffer_state();
	}

	/* Destroy the stack itself. */
	Dtd_PreParse_free((yy_buffer_stack) );
	(yy_buffer_stack) = NULL;

    /* Reset the globals. This is important in a non-reentrant scanner so the next time
     * Dtd_PreParse_lex() is called, initialization will occur. */
    yy_init_globals( );

    return 0;
}

